    private void BttingresarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_BttingresarMouseClicked
        String usuario= txadministrador.getText();
        String contraseña= txcontraseña.getText();
        if(usuario.equals("Jose_Blanco")&& contraseña.equals("202100250")){
            Informacion administrador= new Informacion ();
            administrador.setVisible(true);
            this.dispose();
        }else{
            JOptionPane.showMessageDialog(null,"USUARIO O CONTRASEÑA \n INCORRECTOS");
        }
    }//GEN-LAST:event_BttingresarMouseClicked

